mapper.update:
update tbl_user
set
userpw = #{userpw, jdbcType=VARCHAR}, username = #{username,
jdbcType=VARCHAR}, nickname = #{nickname, jdbcType=VARCHAR}, email =
#{email, jdbcType=VARCHAR}
where mno = #{mno, jdbcType=INTEGER}

mapper.select:
select *
from tbl_user
where userid=#{userid} and userpw=#{userpw}
